import React from "react";

//import images from assets folder HERE "example: import picture from '../assets/images/picture.jpg"

class Carousel extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        {
          id: 0,
          title: "Placeholder0",
          subTitle: "Placeholder0",
          imgSrc: Image,
          link: "link",
          selected: false,
        },
        {
          id: 1,
          title: "Placeholder1",
          subTitle: "Placeholder1",
          imgSrc: Image,
          link: "link",
          selected: false,
        },
        {
          id: 2,
          title: "Placeholder2",
          subTitle: "Placeholder2",
          imgSrc: Image,
          link: "link",
          selected: false,
        },
      ],
    };
  }

  handleCardClick = (id, card) => {
    let items = { ...this.state.items };

    items[id].selected = items[id].selected ? false : true;

    items.forEach((item) => {
      if (item.id !== id) {
        item.selected = false;
      }
    });

    this.setState({
        items
    })
  };

  render() {
    return <p> Carousel works! </p>;
  }
}
export default Carousel;
